let cfar=document.querySelector("#cfAR");let custom_bump_prefix="custom-bump-";let add_button_data_title="btn-custom-bump-add-";let remove_button_data_title="btn-custom-bump-remove-";document.querySelectorAll('[data-title^="'+custom_bump_prefix+'"]').forEach(el=>{let product_id=el.dataset.title.split("-").at(-1);let add_btn=el.querySelector("[data-title^="+add_button_data_title+"] a");if(typeof add_btn!=="undefined"){add_btn.addEventListener("click",e=>{let product_element=cfar.querySelector('input[type="checkbox"][name="purchase[product_ids][]"][value="'+product_id+'"]');product_element.setAttribute("checked","checked");window.cfpeRebuildOrderSummary()})}let remove_btn=el.querySelector("[data-title^="+remove_button_data_title+"] a");if(typeof remove_btn!=="undefined"){remove_btn.addEventListener("click",e=>{let product_element=cfar.querySelector('input[type="checkbox"][name="purchase[product_ids][]"][value="'+product_id+'"]');product_element.removeAttribute("checked");window.cfpeRebuildOrderSummary()})}});